var currentDateTime = now();
// CF2016+ ONLY
// Outputs 2020-01-12T16:22:19Z
writeDump( dateTimeFormat( currentDateTime, "iso" ) );
// Lucee-ONLY
// Outputs 2020-01-12T16:12:09+0000
writeDump( dateTimeFormat( currentDateTime, "iso8601" ) );
// Supports Lucee and CF2016+
// Outputs 2020-01-12T16:22:19Z
writeDump( dateTimeFormat( currentDateTime, "YYYY-MM-DD'T'HH:NN:SSXXX" ) );
